import React, {Component} from 'react';
import './Display.css'

class info extends Component{

    state = {
        name:"",
        age:"",
        job:"",
    }

    handlechange= (e) =>{
        console.log(e.target.id);
        this.setState({
            
           
            [e.target.id]: e.target.value
        
        })
    }

    handlesubmit = (e) =>{
        e.preventDefault();
        console.log(this.state);
    }

    render(){
        return(
            <div className='person'>
        <form>
            <label htmlFor='name'>Name:</label>
            <input type='text' id='name' placeholder='please enter name' onChange={this.handlechange}></input><br/>
            <label htmlFor='age'>Age:</label>
            <input type='text' id='age' placeholder='please enter age' onChange={this.handlechange}></input><br/>
            <label htmlFor='job'>Job:</label>
            <input type='text' id='job' placeholder='please enter your last job' onChange={this.handlechange}></input><br/>
            ,<input type="submit" value = 'add person' onClick={this.handlesubmit}/>
        </form>
        </div>
        )}

}

export default info;
