import React from 'react';
import './Display.css'


//fuctional component/ UI
const Display = ({people, deletePerson, weather}) => {


    
    //console.log(props);

    //let {name,age,job} = props
    //console.log(people)
    const peoplelist = people.length ? people.map( person =>{
        var present = new Date();
        const hour= present.getHours();
        const minute = present.getMinutes();
        const day = present.getUTCDay();
        switch(day){
            case 1:monday = {day};break;
            case 2:tuesday = {day};break;
            case 3:tuesday = {day};break;
            case 4:tuesday = {day};break;
            case 5:friday = {day};break;
            case 6:tuesday = {day};break;
            case 7:tuesday = {day};break;
        }

        let {title,woeid} = person;
        let{max_temp,min_temp,humidity,the_temp,applicable_date,weather_state_name,weather_state_abbr}=weather;
    return(
        
        <div key={woeid}>
            <h1 className="date">today's date:{applicable_date}<br/>Day:{day}</h1>
            <h1 className='city'>Name:{title}</h1>
    <p className="diffrence">max_temp:{max_temp}</p>   
    <p className="diffrence">min_temp:{min_temp}</p> 
    <p className="diffrence">humidity:{humidity}</p>
    <h1 classname="header">current_temp:{the_temp}</h1>
    
   
    
    <img className='image' src={`https://www.metaweather.com/static/img/weather/${weather_state_abbr}.svg`}  alt = ""name="image"></img>
    <h1>{weather_state_name}</h1>


                   </div>
    )
    }):<p></p>

    //console.log(peoplelist);

    return(
        <div>
            {peoplelist}
        </div>
    );
}

export default Display;
