import './Display.css'



const Display = ({people}) => {
    //console.log(props);

    //let {name,age,job} = props
    console.log(people)
    const peoplelist = people.map( person =>{
        if (person.length > 0){
        let {name, age, job, id} = person;
    return(
        <div className='person' key={id}>
            <h1>Name:{name}</h1>
            <h2>Age:{age}</h2>
            <h3>job:{job}</h3>
                   </div>
    )
    }else{
        return(
            <div classname='name'>
                <h1>no data sorry</h1>
            </div>
        )
    }
})

    console.log(peoplelist);

    return(
        <div>
            {peoplelist}
        </div>
    );
}

export default Display;
