import React from 'react';
//import { Map, GoogleApiWrapper, Marker  } from 'google-maps-react';
import './App.css';
import Display from './components/display';
import Info from './components/info';
import axios from 'axios';

class App extends React.Component{
  state={ image:{},
          description: [],
          cryptocurrency:[],
          homepage:[],
          market_data:[],
          tickers:[]

}


addPerson = (newPerson) =>{

 let newarr = [newPerson];
 console.log(newarr)

 this.setState({
   city : newarr
 })
 console.log(newarr[0])
 const cors = `https://cors-anywhere.herokuapp.com/` 
  axios.get(cors+`https://api.coingecko.com/api/v3/coins/bitcoin`)
   .then( resolve => {
     if(resolve.data.length < 1)
     {//console.log(resolve.data);
     alert("please search with perfect spelling");
    }
            else{

              switch(newarr[0].name)
              {
                default:var lang='en';break;
                case "korean": lang='ko';break;
                case "Arabic": lang='ar';break;
              }

     console.log(resolve.data);

     this.setState({
       description : resolve.data.description[`${lang}`],
       image : resolve.data.image,
       cryptocurrency: resolve.data.categories,
       homepage:[resolve.data.links.homepage[0],resolve.data.links.repos_url.github[0]], 
       tickers:resolve.data.tickers,
       market_data:resolve.data.market_data.current_price 

      })
    }}
    )
}


componentDidUpdate(){
  console.log(this.state.tickers)
  console.log(this.state.description)
  }

  render(){
    let har,par,char,dhar,thar,lar,nar,bhar;
   if(this.state.description.length!=0)
   {
   har= <h2>{this.state.cryptocurrency[0]}</h2>;
   par= <img src={this.state.image.large} alt=""></img>;
   char= <h1>Description: {this.state.description}</h1>;
    thar= <p>{this.state.market_data.aed}</p>;
    dhar= <a href={this.state.homepage[0]}>click here to go to homepage</a>;
    bhar=<br/>;
    lar=<a href={this.state.homepage[1]}>click here to go to repos_url</a>;
    nar=<Display tickers={this.state.tickers}></Display>;
   }
  return (
    <div className="App">
      <h1 className='center purple-text'>BITCOIN-APP</h1>
      <Info addPerson={this.addPerson}></Info>
      {har}
      {par}
      {char}
      {thar}
      {dhar}{bhar}
      {lar}
      {nar}


    </div>
  );
}
}

export default App;
