import React from 'react';
import './Display.css'


//fuctional component/ UI
const Display = ({people, deletePerson}) => {
    //console.log(props);

    //let {name,age,job} = props
    //console.log(people)
    const peoplelist = people.length ? people.map( person =>{
      
        let {name, age, job, id} = person;
    return(
        <div className='person' key={id}>
            <h1>Name:{name}</h1>
            <h2>Age:{age}</h2>
            <h3>job:{job}</h3>
            <button onClick={deletePerson(id)}>delete</button>
                   </div>
    )
    }) : (<div className = 'person'><h1>no available data</h1></div>)

    //console.log(peoplelist);

    return(
        <div>
            {peoplelist}
        </div>
    );
}

export default Display;
