import React from 'react';
import './Display.css'


//fuctional component/ UI
const Display = ({people, deletePerson, weather}) => {


    
handlesubmit = (e) =>{
    e.preventDefault();
       // console.log(this.state);
    this.props.addPerson(this.state);
        
    }

    //console.log(props);

    //let {name,age,job} = props
    //console.log(people)
    const peoplelist = people.length ? people.map( person =>{
      
        let {title,woeid} = person;
        let{max_temp,min_temp,humidity,the_temp,applicable_date,weather_state_name}=weather;
        
    return(
        
        <div className='person' key={woeid}>
            <h1>today's date:{applicable_date}</h1>
            <h1 className='CITY'>Name:{title}</h1>
    <h2>max_temp:{max_temp}</h2>   
    <h3>min_temp:{min_temp}</h3> 
    <h3>humidity:{humidity}</h3>
    <h3>current_temp:{the_temp}</h3>

    
    <img className='image' src="https://www.metaweather.com//static/img/weather/s.svg" name="image"></img>
    <h1>{weather_state_name}</h1>


                   </div>
    )
    }) : ( 
        
            <div className='person'>
        <form onSubmit={handlesubmit}>
            <label htmlFor='name'>Name:</label>
            <input type='text' id='name' placeholder='please enter city' onChange={this.handlechange}></input><br/>
        </form>
        </div>
    )

    //console.log(peoplelist);

    return(
        <div>
            {peoplelist}
        </div>
    );
}

export default Display;
