import React from 'react';

import './App.css';
import Display from './components/display';
import Info from './components/info';
import axios from 'axios';

class App extends React.Component{
  state={ people: [],
          weather:[],
}
addPerson = (newPerson) =>{
  newPerson.id = Math.floor(Math.random()*10);
  //console.log(newPerson);
 // this.state.people.push(newPerson) never update state where you change the original value
 let newarr = [...this.state.people, newPerson];

 this.setState({
   people : newarr
 })
}

deleteperson = (woeid) =>{
  console.log(woeid);
  let newPeople = this.state.people.filter( person =>{
    return person.woeid !== woeid 
  });
  console.log(newPeople);
  this.setState({
    people : newPeople
  })
}

componentDidMount(){
  console.log("hi")
  //var date = new Date();
  //var ts = date.getTime();


  const cors = `https://cors-anywhere.herokuapp.com/` 
  axios.get(cors+'https://www.metaweather.com/api/location/search/?query=New Delhi')
   .then( resolve => {
     console.log(resolve.data);
     this.setState({
       people : resolve.data
     })
     //
    // for(var i=0;i<11.;i++){
    axios.get(cors+`https://www.metaweather.com/api/location/${this.state.people[0].woeid}`)
    .then( resolve => {
      console.log(resolve.data.consolidated_weather[0]);
      resolve.data.consolidated_weather[0].max_temp = resolve.data.consolidated_weather[0].max_temp.toFixed(2);
      this.setState({
        weather : resolve.data.consolidated_weather[0]
      })
    //  for(var i=0;i<11;i++){
    })


   }
   )
}

//componentDidUpdate(prevProp, PrevState){
  //console.log("yes it is")
  //console.log(prevProp, PrevState);
  //console.log(this.state)
//}
  render(){
  return (
    <div className="App">
      <h1 className='center purple-text'>WEATHER-APP</h1>
      <Display people = {this.state.people} weather = {this.state.weather} deletePerson = {this.deleteperson}></Display>
      <Info addPerson={this.addPerson}></Info>
    </div>
  );
}
}

export default App;
